import { useState,useEffect } from "react"
import { Container, Row,Col } from "react-bootstrap"
import { ArrowRightCircle } from 'react-bootstrap-icons';
import headerImg from "../assets/img/header-img.svg";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Banner =()=>{

    const [loopNum, setLoopNum]= useState(0);
    const [isDeleting,setIsDeleting] = useState(false);
    const toRotate=["Web Developer","App developer", "UI/UX Designer"];
    const [text,setText]=useState('');
    const [delta,setDelta]=useState(300-Math.random()*100)
    const [index, setIndex] = useState(1);
    const period= 2000;


    useEffect(()=>{
        let ticker= setInterval(()=>{
            tick();
        },delta)

        return(()=> clearInterval(ticker))
    },[text])

    const tick=()=>{
        let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta(prevDelta => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex(prevIndex => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === '') {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex(prevIndex => prevIndex + 1);
    }
    }
    return(
        // <section className="banner" id="home">
        //     <Container>
        //         <Row>
        //             <Col xs={12} md={6} xl={7}>
        //                 <span className="tagline">Welcome to my portfolio</span>
        //                 <h1>{`Hi I am Harshit`}<span className="wrap">{text}</span></h1>
        //                 <p>
        //                     Lorem cdvfdvfd vfvfvfr gffrv fv vfrv frvb gfbv gfb grb gbgr b gt rgb rgb rg b rb rb t rb rtb r br br rb
        //                 </p>
        //                 <button onClick={() => console.log('connect')}>Let’s Connect <ArrowRightCircle size={25} /></button>
        //             </Col>
        //             <Col xs={12} md={6} xl={5}>
        //                 <img src={headerImg} ></img>
        //             </Col>
        //         </Row>
        //     </Container>
        // </section>
        <section className="banner" id="home">
      <Container>
        <Row className="aligh-items-center">
          <Col xs={12} md={6} xl={7}>

                <span className="tagline">Welcome to my Portfolio</span>
                <h1>{`Hi! I'm Harshit`} <span className="txt-rotate" dataPeriod="1000" data-rotate='[ "Web Developer", "Web Designer", "UI/UX Designer" ]'><span className="wrap">{text}</span></span></h1>
                  <p>I design and code beautifully simple things, and I love what I do.</p>
                  <button onClick={() => console.log('connect')}>Let’s Connect <ArrowRightCircle size={25} /></button>


          </Col>
          <Col xs={12} md={6} xl={5}>


             <img src={headerImg} alt="Header Img"/>

          </Col>
        </Row>
      </Container>
    </section>
    )

}
