import { useState,useEffect } from "react"
import {Navbar, Container,Nav } from "react-bootstrap"
import logo from '../assets/img/logo.svg'
import icon1 from '../assets/img/nav-icon1.svg'
import icon2 from '../assets/img/nav-icon2.svg'
import icon3 from '../assets/img/nav-icon3.svg'

export const NavBar = () =>{
    const [activeLink, setActiveLink] = useState('home');
    const [scrolled,setScrolled]=useState(false);

    useEffect(()=>{
        const onScroll = () =>{
            if(window.scrollY>50){
                setScrolled(true);
            }else{
                setScrolled(false);
            }
        }
        window.addEventListener("scroll",onScroll);

        return ()=> window.removeEventListener("scroll",onScroll);
    },[]) 

    const onUpdateActiveLink =(Active) =>{
        setActiveLink(Active);
    }
    return (
        <Navbar  expand="md" className={scrolled ? "scrolled":""}>
        <Container>
        <Navbar.Brand href="/"><img src={logo} /></Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav"><span className="navbar-toggler-icon"></span></Navbar.Toggle>
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home" className={activeLink==='home' ? 'active navbar-link':'navbar-link'} onClick={()=> onUpdateActiveLink('home')}>Home</Nav.Link>
            <Nav.Link href="#skills" className={activeLink==='skills' ? 'active navbar-link':'navbar-link'} onClick={()=> onUpdateActiveLink('skills')}>Link</Nav.Link>
            <Nav.Link href="#projects" className={activeLink==='Project' ? 'active navbar-link':'navbar-link'} onClick={()=> onUpdateActiveLink('Peojects')}>Projects</Nav.Link>

          </Nav>
          <span className="navbar-text">
            <div className="social-icon">
                <a><img src={icon1}></img></a>
                <a><img src={icon2}></img></a>
                <a><img src={icon3}></img></a>
            </div>
            <button className="vvd" onClick={()=>console.log("clicked")}> <span>Let's CONNECT</span></button>
          </span>
        </Navbar.Collapse>
      </Container>
